<div class="item">	<input type="hidden" name="REQUIRED" value="0"><label><input type="checkbox" name="REQUIRED" value="1"<?	if($_POST['REQUIRED']){		?> checked="checked"<?	}	?>>Обязательное для заполнение</label></div><div class="item">	<div class="name">Сущность</div>	<div class="field"><select name="SETTING[TYPE]" id="type"><?	$i=0;	$arSettingType = array('Не указывать', 'Целое число', 'Дробное число', 'Ссылка', 'E-mail', 'Дата');	foreach($arSettingType as $sValue)	{		?><option value="<?=$i?>"<?		if($_POST['SETTING']['TYPE'] == $i){			?> selected="selected"<?		}		?>><?=$sValue?></option><?		$i++;	}	?></select></div></div><div class="item" id="default_date"<?	if($_POST['SETTING']['TYPE'] != 5){		?> style="display:none"<?	}	?>>	<input type="hidden" name="SETTING[DEFAULT_DATE]" value="0">	<label><input type="checkbox" name="SETTING[DEFAULT_DATE]" value="1"<?	if($_POST['SETTING']['DEFAULT_DATE']){		?> checked="checked"<?	}	?> class="active" id="set_date">Установить текущию дату</label></div><script>$(function(){	$('#type').change(function(){		val = $(this).val();		$('#default').val('');		if(val == 5){			$('#default_date').show();		}else{			$('#default_date').hide();			$('#set_date')[0].checked = false;		}	});});</script>